import React,{useState} from "react";
import Card from "../UI/Card";
import Button from '../UI/Button'
import styles from "./AddUser.module.css";

const AddUser = () => {

  const [userText,setUserText] =  useState('')

  const [numberText,setNumberText] = useState('')

  const handleClick = (event)=>{
    event.preventDefault()
    
    if(userText.trim().length === 0 || numberText.trim().length === 0){
      return;
    }

    if(+numberText<1){
      return;
    }
    console.log(userText,numberText)
    setUserText('')
    setNumberText('')
  }

  const userInputTextHandler = (event)=>{
    setUserText(event.target.value)
  }

  const userAgeTextHandler = (event)=>{
    setNumberText(event.target.value)
  }

  return (
    <Card className={styles.input}>
      <form onClick={handleClick}>
        <label htmlFor="username" >UserName</label>
        <input type="text" onChange={userInputTextHandler} />
        <label htmlFor="age">Age(Years)</label>
        <input type="number" onChange={userAgeTextHandler}/>
        <Button type="submit">Add User</Button>
      </form>
    </Card>
  );
};

export default AddUser;
